import styled from 'styled-components';
import CustomButtom from '../custom-button/custom-button.component';

export const CartDropdownComponent = styled.div`
     position: absolute;
    width: 240px;
    height: 340px;
    display: flex;
    flex-direction: column;
    padding: 20px;
    border: 1px solid black;
    background-color: white;
    top: 90px;
    right: 40px;
    z-index: 5;
`;


export const CartItemsComponents = styled.div`
 height: 240px;
      display: flex;
      flex-direction: column;
      overflow-y: scroll;
`;

export const DropDownButton = styled(CustomButtom)`
  margin: auto;
`;

export const EmptyMessageContainer = styled.span`
font-size: 18px;
      margin: 30px;
`;
