import React from 'react';
import { clearItemFromCart, addItem, removeItem } from '../../redux/cart/cart.actions';
import { connect } from 'react-redux';
import { CheckOutItemConatainer, ImageContainer, TextContainer, QuantityContainer, RemoveButtonContainer } from './checkout-item.styles';

const CheckoutItem = ({cartItem, clearItem, addItem, removeItem}) =>{ 
    const {imageUrl, name, price, quantity} = cartItem ;
    return(
    <CheckOutItemConatainer>
        <ImageContainer>
            <img alt='item' src={imageUrl} />
        </ImageContainer>
        <TextContainer>{name}</TextContainer>
        <QuantityContainer>
        <div onClick={() => removeItem(cartItem)}>-</div>
        <span>{quantity}</span>
        <div onClick={() => addItem(cartItem)}>+</div>
        </QuantityContainer>
        <TextContainer>{price}</TextContainer>
        <RemoveButtonContainer onClick={() => clearItem(cartItem)}>&#10005;</RemoveButtonContainer>
    </CheckOutItemConatainer>
);
};

const mapDispatchToProps = dispatch => ({
    clearItem : item => dispatch(clearItemFromCart(item)),
    addItem : item => dispatch(addItem(item)),
    removeItem : item => dispatch(removeItem(item))
});


export default connect(null,
    mapDispatchToProps)(CheckoutItem);
